import React, { useState } from "react";
import Select from 'react-select'

// import './Outfit.css'
let fuerza, velocidad, disparo, pase, tecnica
let gear;

function changeStats (st, sp, sh, pa, te) {
    fuerza = st + fuerza
    velocidad = sp + velocidad
    disparo = sh + disparo
    pase = pa + pase
    tecnica = te + tecnica
}

function change2 (event) {
    changeStats(event.value.strength)
    const result = {strength: fuerza, speed: velocidad, shooting: disparo, passing: pase, technique:tecnica}
    return result
}


const helmet = [
    {label: 'Muscle Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}},
    {label: 'Turbo Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}},
    {label: 'Cannon Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}},
    {label: 'Chain Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}},
    {label: 'Trick Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}},
    {label: 'Bushido Gear', value: {strength: 2, speed:0, shooting:0, passing:0, technique: -2}}
]

const Outfit = (props) => {
    fuerza = props.strength,
    velocidad = props.speed,
    disparo = props.shooting,
    pase = props.passing,
    tecnica = props.technique
    
    if(props.style == "helmet"){
        console.log("helmet modifier");
        gear = helmet
        console.log(gear);
    }
    return (
        <div>
            <Select 
                options={gear}
                onChange = {change2}
            />
        </div>
    )
}
export default Outfit;
