import React, {useEffect, useState, useRef } from 'react'
import {Link} from 'react-router-dom'
import Outfit from './Outfit';
import ProgressBar from './ProgressBar'



let path;
let showOptions = false;
const handleClick = () => {
    console.log("hola");
    showOptions = !showOptions
}

function Stats(props){
    const strength = props.strength
    const speed = props.speed
    const shooting = props.shooting
    const passing = props.passing
    const technique = props.technique
    const element =  document.querySelectorAll('.bar-filler');
    console.log(element);
    return(
        <div className='stats'>
            <li className='character_items'>
                <Link className='character_item_link' to={props.path}>
                    <figure className='character_item_pic-wrap' data-category={props.label}>
                        <img src={props.src} alt='Character Image' className='character_item_img'></img>
                    </figure>
                    <div className='character_item_info'>
                        <h5 className='character_item_text'>{props.text}</h5>
                    </div>
                </Link>
                <div className='changer-points'>
                    <h4 className='standard-attributes'>Standard Attributes</h4>
                    <h4>Strength</h4>
                    <ProgressBar
                        completed= {strength*4}
                    />
                    <h4>Speed</h4>
                    <ProgressBar
                        completed={speed*4}
                    />
                    <h4>Shooting</h4>
                    <ProgressBar
                        completed={shooting*4}
                    />
                    <h4>Passing</h4>
                    <ProgressBar
                        completed={passing*4}
                    />
                    <h4>Technique</h4>                    
                    <ProgressBar
                        completed={technique*4}
                    />
                </div>
                       
                <div className='outfit'>
                    <div className='helmet'>
                        <Outfit
                            style = "helmet"
                            strength = {10}
                            speed = {10}
                            shooting = {10}
                            passing = {10}
                            technique = {10}
                        />
                        <p className='element-title'>Helmet</p>
                    </div>

                    <div className='arms'>
                        <Outfit
                            style = "arms"
                            strength = {10}
                            speed = {10}
                            shooting = {10}
                            passing = {10}
                            technique = {10}
                        />
                        <p className='element-title'>Arms</p>
                    </div>

                    <div className='body'>
                        <Outfit
                            style = "body"
                            strength = {10}
                            speed = {10}
                            shooting = {10}
                            passing = {10}
                            technique = {10}
                        />
                        <p className='element-title'>Body</p>
                    </div>

                    <div className='feet'>
                        <Outfit
                            style = "feet"
                            strength = {10}
                            speed = {10}
                            shooting = {10}
                            passing = {10}
                            technique = {10}
                        />
                        <p className='element-title'>Feet</p>
                    </div>


                </div>
            </li>

            
        </div>

    )
}

export default Stats
