import React, { Component } from 'react';
import Counter from './counter';

class Counters extends Component {
    state = {  
        counters: [
            {id: 1, value:0},
            {id: 2, value:5},
            {id: 3, value:0},
            {id: 4, value:0},
        ]
    } ;
    render() { 
        return (
        <div>
            {/* Hardcodeo */}
            {/* <Counter></Counter>
            <Counter></Counter>
            <Counter></Counter>
            <Counter></Counter> */}
            {this.state.counters.map(counter => 
            <Counter key= {counter.id} value= {counter.value} selected={true}></Counter>)}
        </div>);
    }
}
 
export default Counters;
